(function($){$.fn.typing=function(options){return this.each(function(){listenToTyping(this,options);});};function listenToTyping(elem,options){var settings=$.extend({start:null,stop:null,delay:400},options);var $elem=$(elem),typing=false,delayedCallback;function reactToKeypress(event){if(!typing){typing=true;if(settings.start){settings.start.call(this,event);}}}
$elem.keypress(reactToKeypress);$elem.keydown(function(event){if(event.keyCode===8||event.keyCode===46){reactToKeypress.call(elem,event);}});$elem.keyup(function(event){if(typing){clearTimeout(delayedCallback);delayedCallback=setTimeout(function(){typing=false;if(settings.stop){settings.stop.call(elem,event);}},settings.delay);}});}})(jQuery);
